#!/bin/sh

rm -rf CMakeFiles
rm -f CMakeCache.txt

case "$1" in
--clang)  echo "Clang"
    export CC=/usr/bin/clang
    export CXX=/usr/bin/clang++
    ;;
esac

cmake ../deal.II -DCMAKE_BUILD_TYPE=Release -DCMAKE_POSITION_INDEPENDENT_CODE=ON -DBUILD_SHARED_LIBS=ON -DDEAL_II_COMPONENT_EXAMPLES=ON -DDEAL_II_COMPONENT_MESH_CONVERTER=OFF -DDEAL_II_COMPONENT_PARAMETER_GUI=OFF -DDEAL_II_COMPONENT_DOCUMENTATION=OFF -DDEAL_II_FORCE_BUNDLED_UMFPACK=ON -DDEAL_II_FORCE_BUNDLED_THREADS=ON -DDEAL_II_FORCE_BUNDLED_BOOST=ON
 
# -DCMAKE_POSITION_INDEPENDENT_CODE=ON -DBUILD_SHARED_LIBS=OFF -DDEAL_II_PREFER_STATIC_LIBS=ON 
